void WriteTable201::writeTable()
{
    ta_uint32 localCoverageBitmap;
    PasHelpers::getInstance()->coverageToLocalCoverageBitmap(this->m_zones, localCoverageBitmap);
    
    // ReEntrant as this will also be guarded in the write() call.
    ThreadGuard guard( this->m_table.m_lock ); 
    
    PasHelpers::getInstance()->set8bit (this->m_table.m_buffer, TABLE_201_SOURCE_ID_OFFSET,      m_sourceId);
    PasHelpers::getInstance()->set8bit (this->m_table.m_buffer, TABLE_201_ANNOUNCE_ID_OFFSET,    m_announceId);
    PasHelpers::getInstance()->set32bit(this->m_table.m_buffer, TABLE_201_LOCAL_COVERAGE_OFFSET, localCoverageBitmap);
    
    m_future.set( PASConnection::getInstance()->writeTable(this->m_table.TABLE_NUMBER,
        this->m_table.m_buffer,
        this->m_table.BUFFER_SIZE) );
}

PASWriteEvent* WriteTable201::recreateEvent(ACE_Time_Value timeToExpire)
{
    return new WriteTable201(timeToExpire,
        this->m_table,
        this->m_future,
        this->m_sourceId,
        this->m_announceId,
        this->m_zones,
        this->m_socketScheduler,
        this->m_processScheduler,
        this->m_pasEventSource);
}

